import React, {useState, useEffect} from 'react'
import Popper from 'popper.js'
import Apps from './apps'

export const Dropdown1 = ({placement = 'bottom-end'}) => {
    const [hidden, setHidden] = useState(true)
    const openDropdown = () => {
        new Popper(buttonRef.current, dropdownRef.current, {
            placement: placement
        })
        setHidden(false)
    }
    const closeDropdown = () => {
        setHidden(true)
    }

    const buttonRef = React.createRef()
    const dropdownRef = React.createRef()
    useEffect(() => {
        const handleClickOutside = event => {
            if (
                hidden ||
                buttonRef.current.contains(event.target) ||
                dropdownRef.current.contains(event.target)
            ) {
                return false
            }
            setHidden(!hidden)
        }
        document.addEventListener('mousedown', handleClickOutside)
        return () => {
            document.removeEventListener('mousedown', handleClickOutside)
        }
    }, [hidden, dropdownRef, buttonRef])
    return (
        <div className="hidden lg:flex relative">
            <button
                ref={buttonRef}
                onClick={() => (hidden ? openDropdown() : closeDropdown())}
                className="flex items-center justify-center h-16 w-12">
            </button>
            <div ref={dropdownRef} className={`dropdown ${hidden ? '' : 'open'}`}>
                <div className={`dropdown-content w-64 ${placement}`}>
                    <Apps />
                </div>
            </div>
        </div>
    )
}
