import React, {useState} from 'react'
import {BrowserRouter, Route} from "react-router-dom";
import NewsDetail from "./newsDetail";

const NewsList = () => {
    const [ title, setTitle ]= useState("")
    const [ summary, setSummary ] = useState("")
    const [ keyword, setKeyword ] = useState("")
    const [ regDate, setRegDate] = useState("")


    const moveToDetail = e => {

    }

    return <>
        <div className="w-full p-4 rounded-lg bg-white border border-grey-100 dark:bg-dark-95 dark:border-dark-90">
            <div className="flex flex-row items-center justify-between mb-6">
                <div className="flex flex-col">
                    <div className="text-sm font-light text-grey-500"> NEWS TITLE </div>
                    <div className="text-sm font-bold"><span> SUMMARY </span></div>
                </div>
            </div>

                <div className=" flex flex-col w-full">

                    <div className="outline-style flex flex-row items-center justify-start w-full lg:px-2">
                        <div className="w-full flex items-start justify-start ">
                            <div className="w-full ">
                                <div className="text-sm font-bold" onClick={moveToDetail}> {title} </div>
                                <div className="text-xs text-grey-500"> {summary} </div>
                                <span className="badge badge-sm bg-cyan-700 text-cyan-100 "> {keyword} </span></div>
                                {/*키워드 컬러 지정 : text-cyan-100*/}
                            <div className="ml-auto mr-1 lg:mr-2 whitespace-no-wrap text-xs text-grey-500"> {regDate} </div>
                        </div>
                    </div>
                    
                </div>

        </div>
    </>
}
export default NewsList
