import React from 'react'
import PropTypes from 'prop-types'
import '../../css/components/badges.css'

export const Badge = ({
                          rounded = false,
                          outlined = false,
                          size = 'default',
                          color,
                          children
                      }) => {
    let css = []
    css.push(color)
    if (rounded) css.push('rounded-lg')
    css = css.join(' ')
    if (outlined) {
        return (
            <span className={`badge badge-${size} badge-outlined ${css}`}>
        {children}
      </span>
        )
    }
    return <span className={`badge badge-${size} ${css}`}>{children}</span>
}
Badge.propTypes = {
    size: PropTypes.oneOf(['sm', 'default', 'lg']),
    rounded: PropTypes.bool,
    outlined: PropTypes.bool,
    color: PropTypes.string,
    children: PropTypes.any
}

export const CircularBadge = ({
                                  size = 'default',
                                  outlined = false,
                                  color,
                                  children
                              }) => {
    let css = []
    css.push(color)
    css = css.join(' ')
    if (outlined) {
        return (
            <span
                className={`badge badge-circle badge-${size} badge-outlined ${css}`}>
        {children}
      </span>
        )
    }
    return (
        <span className={`badge badge-circle badge-${size} ${css}`}>
      {children}
    </span>
    )
}

CircularBadge.propTypes = {
    size: PropTypes.oneOf(['sm', 'default', 'lg']),
    color: PropTypes.string,
    children: PropTypes.any
}
